# Listing 8-0 ######### This code sets up the R environment for the chapter# set working directory to chapter location# (change for where you set up files in ch 2)setwd("~/book/ch08")# make sure the packages for this chapter# are installed, install if necessarypkg <- c("RBerkeley")new.pkg <- pkg[!(pkg %in% installed.packages())]if (length(new.pkg)) {  install.packages(new.pkg)  }# Listing 8-2 ####### R code to interface with BDBlibrary(RBerkeley)# create and open BDB databasedbh <- db_create()db <- db_open(dbh, txnid = NULL, file = "av.db", type = "BTREE", flags = mkFlags(DB_CREATE, DB_EXCL))# store geolocation datadb_put(dbh, key = charToRaw("24.62.253.107"), data = charToRaw("43.2555,-70.8829"))# read it back to show it workscoords <- rawToChar(db_get(dbh, key = charToRaw("24.62.253.107")))db_close(dbh) # close BDB dbprint(coords)## [1] "43.2555,-70.8829"